#include <stdio.h>
#include <string.h>

int day1_2015_1(const char *s) {
    int result = 0;
    for (int i = 0; i < strlen(s); i++) {
        if (s[i] == '(') {
            result += 1;
        } else {
            result -= 1;
        }
    }
    return result;
}

int day1_2015_2(const char *s) {
    int result = 0;
    for (int i = 0; i < strlen(s); i++) {
        if (s[i] == '(') {
            result += 1;
        } else {
            result -= 1;
        }
        if (result == -1) {
            return i + 1;
        }
    }
    return -1;
}

