def day1_2015_1(s):
    result = 0
    for c in s:
        if c == "(": result += 1
        else: result -= 1

    return result

def day1_2015_2(s):
    result = 0

    for i in range(len(s)):
        if c == "(": result += 1
        else: result -= 1

        if result == -1: return i+1

    return -1

