fn day1_2015_1(s: &str) -> i32 {
    let mut result = 0;
    for c in s.chars() {
        if c == '(' {
            result += 1;
        } else {
            result -= 1;
        }
    }
    result
}

fn day1_2015_2(s: &str) -> Option<usize> {
    let mut result = 0;
    for (i, c) in s.chars().enumerate() {
        if c == '(' {
            result += 1;
        } else {
            result -= 1;
        }

        if result == -1 {
            return Some(i + 1);
        }
    }
    None
}

